import Layout from '../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Index = props => (
  <Layout>
      <h1>{props.data}</h1>
  </Layout>
);

Index.getInitialProps = async function() {
  const res = await fetch('https://localhost:8000/api/query/getRecipeById', {
      method: 'POST',
      headers: {
          'Content-Type': 'application-json'
      },
      body: JSON.stringify({recipeId: '559251'})
  });
  const data = await res.json();

  console.log(data);

  return {
    data
  };
};

export default Index;
